import React, { useState, useEffect } from "react";
import { Flex, Button } from "rebass";
import io from "socket.io-client";

const uploadEvent = data => {
  const fReader = new FileReader();
  const socket = io("http://localhost:5000");

  fReader.onload = event => {
    socket.emit("Start", { Name: data.name, Size: data.size });
  };
};

const Upload = params => {
  const [fileData, setFile] = useState(null);
  const [socket, setSocket] = useState(null);

  useEffect(() => {}, []);

  return (
    <Flex
      style={{ height: "100vh" }}
      alignItems="center"
      justifyContent="center"
    >
      {fileData && fileData.name}
      <input type="file" accept="video/*" onChange={setFile} />
      {fileData && <Button onClick={uploadEvent(fileData)}> Upload </Button>}
    </Flex>
  );
};

export default Upload;
