import React, { useState, useEffect } from "react";
import { Flex, Button } from "rebass";
import io from "socket.io-client";

const uploadEvent = data => {
  const fReader = new FileReader();

  const socket = io("http://localhost:5000");

  fReader.onload = event => {
    console.log(event);
    socket.emit("Start", { Name: data.name, Data: event.target.result });
  };

  fReader.readAsArrayBuffer(data);
  socket.emit("Start", { Name: data.name, Size: data.size });
};

const Upload = params => {
  const [fileData, setFile] = useState(null);

  useEffect(() => {}, []);
  console.log(fileData);
  return (
    <Flex
      style={{ height: "100vh" }}
      alignItems="center"
      justifyContent="center"
    >
      <input
        type="file"
        accept="video/*"
        onChange={e => setFile(e.target.files[0])}
      />
      {fileData && (
        <Button onClick={() => uploadEvent(fileData)}> Upload </Button>
      )}
    </Flex>
  );
};

export default Upload;
